INSERT INTO `templates` VALUES ('3', 'review', '<h3>ĐẲNG CẤP PH&Ograve;NG THU.</h3>\r\n\r\n<ul>\r\n <li>&Acirc;m thanh đ&aacute;p ứng được ti&ecirc;u chuẩn c&aacute;c ph&ograve;ng thu hiện đại. Một sự lựa chọn tuyệt vời cho c&aacute;c d&ograve;ng nhạc s&ocirc;i động, tươi trẻ hiện nay.</li>\r\n</ul>\r\n\r\n<h3>&Acirc;M CAO TRONG TRẺO V&Agrave; TIẾNG BASS UY LỰC</h3>\r\n\r\n<ul>\r\n  <li>Vỏ ngo&agrave;i tai nghe l&agrave;m từ nh&ocirc;m v&agrave; th&eacute;p, gi&uacute;p &acirc;m bass trở n&ecirc;n chắc khỏe, mạnh mẽ v&agrave; &acirc;m cao r&otilde; r&agrave;ng, trong vắt.</li>\r\n</ul>\r\n\r\n<h3>THIẾT KẾ SANG TRỌNG, HIỆN ĐẠI</h3>\r\n\r\n<ul>\r\n  <li>Với một sản phẩm cao cấp như Beats Pro, Monster kh&ocirc;ng cho sử dụng bất kỳ chi tiết n&agrave;o bằng nhựa, tất cả c&aacute;c khớp nối v&agrave; khung tai nghe đều l&agrave;m từ kim loại. Ngay cả d&acirc;y dẫn cũng c&oacute; lớp bọc ngo&agrave;i đẹp v&agrave; bền.</li>\r\n</ul>\r\n\r\n<h3>KHẢ NĂNG CHỐNG ỒN HIỆU QUẢ</h3>\r\n\r\n<ul>\r\n <li>Beats Pro l&agrave; một tai nghe chống ồn thụ động với lớp bọc da của phần ốp tai rất d&agrave;y v&agrave; &ecirc;m &aacute;i, gi&uacute;p &ocirc;m trọn v&agrave;nh tai v&agrave; tạo ra một kh&ocirc;ng gian nghe nhạc y&ecirc;n tĩnh tuyệt đối</li>\r\n</ul>\r\n\r\n<h3>Beats Pro&trade; on ear headphones</h3>\r\n\r\n<table>\r\n <tbody>\r\n   <tr>\r\n      <td><strong>&nbsp;Cable</strong></td>\r\n     <td>\r\n      <ul>\r\n        <li>4.2mm coiled cable with 3.5mm twist lock plug</li>\r\n        <li>In-line remote &amp; mic cable (features may vary)</li>\r\n     </ul>\r\n     </td>\r\n   </tr>\r\n   <tr>\r\n      <td>&nbsp;<strong>Adapter</strong></td>\r\n     <td>&nbsp;&frac14;&quot; audio adapter</td>\r\n   </tr>\r\n   <tr>\r\n      <td>&nbsp;<strong>Accessories</strong></td>\r\n     <td>\r\n      <ul>\r\n        <li>Carrying pouch</li>\r\n       <li>Beats cleaning cloth</li>\r\n     </ul>\r\n     </td>\r\n   </tr>\r\n   <tr>\r\n      <td><strong>&nbsp;Weight (kg)</strong></td>\r\n     <td>&nbsp;0.40</td>\r\n   </tr>\r\n   <tr>\r\n      <td><strong>&nbsp;Height (mm)</strong></td>\r\n     <td>&nbsp;197</td>\r\n    </tr>\r\n   <tr>\r\n      <td><strong>&nbsp;Length of Cable (m)</strong></td>\r\n     <td>&nbsp;1.523</td>\r\n    </tr>\r\n   <tr>\r\n      <td><strong>&nbsp;Type of Jack</strong></td>\r\n      <td>&nbsp;3.5mm</td>\r\n    </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p><a href=\"http://demandware.edgesuite.net/aajh_prd/on/demandware.static/Sites-beats-Site/Sites-beats-master-catalog-en/default/v1383896974663/pdf/beats-pro-user-guide-americas.pdf\">User Guide</a></p>\r\n', 'N', '2016-04-30 17:20:34', '2016-06-17 19:23:55');
