INSERT INTO `products` VALUES ('22', 'Sản phẩm demo 14', '343', 'san-pham-demo-14', 'speaker', '21.000.000đ', '20.000.000 ₫', '44', '', 'dj,the-thao,choi-game', '2016-07-16', 'Còn hàng', '<p>111111111111111</p>\r\n', '<p><img alt=\"beats solo 2 wireless gold 01\" src=\"http://plantronicschinhhang.com/wp-content/uploads/beats-solo-2-wireless-gold-01.jpg\" style=\"height:720px; width:960px\" /></p>\r\n\r\n<p><img alt=\"Beats-Solo2-Wireless-Apple-Colors\" src=\"http://plantronicschinhhang.com/wp-content/uploads/Beats-Solo2-Wireless-Apple-Colors.jpg\" style=\"height:600px; width:980px\" /></p>\r\n\r\n<h2><strong>Thiết kế của tương lai</strong></h2>\r\n\r\n<p>Tai nghe <strong>Beats Solo 2 Wireless</strong> c&oacute;&nbsp;chất lượng ho&agrave;n thiện sản phẩm tốt, thiết kế tinh tế, miếng đệm si&ecirc;u nhẹ đeo rất &ecirc;m tai.Tai nghe sử dụng c&ocirc;ng nghệ <strong>Bluetooth 4.0</strong> mới nhất, kết nối trong phạm vi 10m với thời lượng pin l&ecirc;n đến 12 giờ.</p>\r\n\r\n<p>Tương th&iacute;ch ho&agrave;n to&agrave;n thiết bị ph&aacute;t nhạc ipod, iphone, ipad. Kết nối dễ d&agrave;ng với c&aacute;c thiết bị m&aacute;y nghe nhạc, điện thoại di động, laptop, pc,&hellip; Chất &acirc;m tinh tế ph&ugrave; hợp với nhiều d&ograve;ng nhạc pop, dance, hihop, trữ t&igrave;nh,&hellip;</p>\r\n\r\n<h2><strong>Kết nối th&ocirc;ng minh</strong></h2>\r\n\r\n<p>Tai nghe&nbsp;<strong>Beats&nbsp;Solo2</strong> Wireless l&agrave; sản phẩm mới đầu ti&ecirc;n được ph&aacute;t h&agrave;nh bởi Beats kể từ khi trở th&agrave;nh một phần của <strong>Apple</strong> sau thương vụ 3 tỷ $. C&aacute;c tai nghe mới được thiết lập sẽ c&oacute; sẵn c&aacute;c bản m&agrave;u đỏ, đen, trắng, xanh, gray, gold, silver.</p>\r\n\r\n<p>Với c&ocirc;ng nghệ Bluetooth mới nhất , Beats Solo2 Wireless cho ph&eacute;p di chuyển tự do trong v&ograve;ng 30 feet(c&aacute;ch xa nguồn ph&aacute;t 10m). Người d&ugrave;ng c&oacute; thể nhận cuộc gọi, bỏ qua c&aacute;c b&agrave;i h&aacute;t, v&agrave; thay đổi &acirc;m lư